const initialState = {
    login: false,
    products : []
  };

  function rootReducer(state = initialState, action) {
    if (action.type === 'LOGIN_STAT') {
        state.login = true
    } else if(action.type === 'PROD_LIST') {
      state.products = [...state.products,action.payload]
    }
    return state;
  };

  export default rootReducer;
