import React from 'react';

import { connect } from "react-redux";
import { getProduct } from '../Apis/callApis';
import { Cardlist } from '../Elements/Cardlist';


import { Link } from 'react-router-dom';


class Products extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            prodData:[]
        }
    }

    componentDidMount() {
        if(!this.props.login) {
            this.props.history.push('/');   
        }
        getProduct().then((data)=>{
            console.log('mount' + data);
            if(data.data.status) {
                console.log(data);
                this.setState({prodData:data.data})
            }

        }).catch((err)=>{
            console.log(err);
        })
    }

    render() {
        console.log(this.state);
        return (
            <>
                <h1>Product Page</h1>
                <Link to="/addprod"><h4>Go To Add Products</h4></Link>

                {this.state.prodData.map((datas,index)=>{
                    return <Cardlist key={index} name={datas.product_name} price={datas.product_price} image={datas.image} descr={datas.descr} />
                })}

            </>
        );
    }

}

const mapStateToProps = state => {
    return { login: state.login };
};

export default connect(mapStateToProps)(Products);
